// Home.tsx
import React, {useState, useRef, useEffect} from 'react';
import About from './About';
import Main from './Main';
import "../css/gravity.css"
import gravity2 from "../img/gravity2.jpg"
import gravity from "../img/gravity.webp"


function Home() {
    const [currentPage, setPage] = useState("");

    const renderPage = () => {
        switch(currentPage){
            case "about":
                return <About />;
            case "main":
                return <Main />;
            default:
               return <>
                    <div>
                        <button onClick={() => setPage("main")}>メイン</button>
                        <Images />
                        <Info />
                    </div>
                </>
        }
    }

    return (
        <>
            {renderPage()}
        </>
    );
}

function Images(){
    return(
        <div className='imgs'>
            <img className="main" src={gravity}></img>
            <img className='main' src={gravity2}></img>
        </div>
    );
}

const Info: React.FC = () =>
{
    const infoRef = useRef<HTMLDivElement>(null);
    const [isVisibel, setIsVisible] = useState(false);

    
    useEffect(() => {
        const handleScroll = () => {
            if(window.scrollY >= 300){
                setIsVisible(true);
            }
            else{
                setIsVisible(false);
            }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        }
    }, []);

    return(
        <div className={`info ${isVisibel ? "visible" : ""}`} ref={infoRef}>
            <p>jsdklfjalskdfj</p>
            <p>fjaslkdajfldks</p>
            <p>alksdjfalkfaff</p>
        </div>
    )
}

export default Home;
