// About.tsx
import React, { useState } from 'react';
import axios from "axios";

const Bort: React.FC = () => {

    const [selectedGame, setSelectedGame] = useState("");
    const [successMessage, setSuccessMessage] = useState("");

    const handleGameSelect = (game:string) => {
        setSelectedGame(game);

        axios.post("/select-game", {game})
        .then(() => {
            setSuccessMessage("Game selection recorded successfully");
        })
        .catch((error) => {
            console.error("Error recording game selection:", error)
            setSuccessMessage("Failed to record game selection");
        })
    }

    return(
        <div>
            <h1>投票</h1>
            <p>選択されたゲーム: {selectedGame}</p>
            <button onClick={() => handleGameSelect("Game1")}>Game1</button>
            <button onClick={() => handleGameSelect("Game2")}>Game2</button>
            <p>{successMessage}</p>
        </div>
    )
}

export default Bort
