import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Game {
    game: string;
    count: number;
}

function Bort() {
    const [selectedGame, setSelectedGame] = useState<string>('');
    const [successMessage, setSuccessMessage] = useState<string>('');
    const [games, setGames] = useState<Game[]>([]);

    useEffect(() => {
        // 投票数を取得
        axios.get('http://localhost:5000/game_selection')
            .then((response) => {
                setGames(response.data);
            })
            .catch((error) => {
                console.error('Error fetching game selections:', error);
            });
    }, []);

    const handleGameSelect = (game: string) => {
        setSelectedGame(game);

        axios.post('http://localhost:5000/game_selection', { game })
            .then(() => {
                setSuccessMessage('Game selection recorded successfully');
                // 投票数を更新
                return axios.get('http://localhost:5000/game_selection');
            })
            .then((response) => {
                setGames(response.data);
            })
            .catch((error) => {
                console.error('Error recording game selection:', error);
                setSuccessMessage('Failed to record game selection');
            });
    };

    return (
        <div>
            <h1>Game Page</h1>
            <p>選択されたゲーム: {selectedGame}</p>
            <button onClick={() => handleGameSelect('Gravity Daze')}>Gravity Dazeを選択する</button>
            <button onClick={() => handleGameSelect('Counter-Strike')}>Counter-Strikeを選択する</button>
            <button onClick={() => handleGameSelect('MonsterHunter WORLD')}>MonsterHunter WORLDを選択する</button>
            <p>{successMessage}</p>
            <h2>投票数</h2>
            <ul>
                {games.map((game) => (
                    <li key={game.game}>{game.game}: {game.count}</li>
                ))}
            </ul>
        </div>
    );
}

export default Bort;
