// Home.tsx
import React, {useState, useRef, useEffect} from 'react';
import About from './About';
import Main from './Main';
import observer from "../functions/observer"
import "../css/gravity.css";
import "bootstrap/dist/css/bootstrap.min.css";
import gravity from "../img/gravity.webp";
import { info } from 'console';


function Home() {
    const [currentPage, setPage] = useState("");

    const renderPage = () => {
        switch(currentPage){
            case "about":
                return <About />;
            case "main":
                return <Main />;
            default:
               return <>
                    <div>
                        <h1 className='header'>Gravity Daze</h1>
                        {/* <button onClick={() => setPage("main")}>メイン</button> */}
                        {/* <Button variant='secondary' onClick={() => setPage("main")}>メイン</Button> */}
                        <Images />
                        <Info />
                        <Video />
                    </div>
                </>
        }
    }

    return (
        <>
            {renderPage()}
        </>
    );
}


function Images(){
    return(
        <div className='imgs'>
            <img className="main" src={gravity}></img>
            {/* <img className='main' src={gravity2}></img> */}
        </div>
    );
}

const Info: React.FC = () =>
{
    const infoRef = useRef<HTMLDivElement>(null);
    const [isVisibel, setIsVisible] = useState(false);

    useEffect(() => {
        const callback = function (entries: IntersectionObserverEntry[], observer: IntersectionObserver) {
            if(entries[0].isIntersecting){
                setIsVisible(true);
            }
            else{
                setIsVisible(false);
            }
        };

        observer(infoRef, callback);
        // const info = new IntersectionObserver(callback);
        // if(infoRef.current)
        // {
        //     info.observe(infoRef.current);
        // }

        // return () => {
        //     if(infoRef.current){
        //         info.unobserve(infoRef.current);
        //     }
        // };
    },[isVisibel]);

    return(
        <section className={`wrapper info ${isVisibel ? "visible" : ""}`}  ref={infoRef}>
            <h2 className='section-head'>紹介</h2>
            <div>
                <p>texttexttexttexttexttexttext</p>
                <p>texttexttexttexttexttexttext</p>
                <p>texttexttexttexttexttexttext</p>
            </div>
        </section>
    )
}

const Video: React.FC = () =>
{
    const videoRef = useRef<HTMLIFrameElement>(null);
    const [isVisibel, setIsVisible] = useState(false);

    useEffect(() => {
        const callback = function (entries: IntersectionObserverEntry[], observer: IntersectionObserver) {
            if(entries[0].isIntersecting){
                setIsVisible(true);
            }
            else{
                setIsVisible(false);
            }
        };

        observer(videoRef, callback);
        }, [isVisibel]);

    return(
        <div>
            <iframe ref={videoRef} className="video" width="560" height="315" src="https://www.youtube.com/embed/xuQJGVXmvvk?si=s4QGKnm4O5S8X5ja" title="YouTube video player" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerPolicy="strict-origin-when-cross-origin" allowFullScreen></iframe>
        </div>
    )
}

export default Home;
