import React, { useRef, useState, useEffect, ReactNode } from "react";
import style from "../../css/Subpage.module.css"
import observer from "../../functions/observer";


interface Props{
    className: string;
    children: string;
}

const Info: React.FC<Props> = ({className, children}) => {
    const ref = useRef<HTMLDivElement>(null);
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const callback = (entries: IntersectionObserverEntry[], observer: IntersectionObserver) => {
            if(entries[0].isIntersecting){
                setIsVisible(true);
            }
            // else{
            //     setIsVisible(false);
            // }
        };

        observer(ref, callback);
    }, [isVisible]);

    const listItems = React.Children.toArray(children)
        .map((child, index) => {
            if (typeof child === 'string') {
                const lines = child.split('\n').map((line, idx) => <li key={idx}>{line}</li>);
                return <React.Fragment key={index}>{lines}</React.Fragment>;
            }
            return child;
        });


    return(
        <section className={`wrapper ${className} ${isVisible ? style.visible : ""}`}>
            <div ref={ref}>
                <h2>情報</h2>
                <ul>
                    {listItems}
                </ul>
            </div>
        </section>
    )
}

export default Info;
