// Home.tsx
import React, {useState, useRef, useEffect} from 'react';
import About from './About';
import Main from './Main';
import "../css/gravity.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Button from "react-bootstrap/Button";
import gravity2 from "../img/gravity2.jpg";
import gravity from "../img/gravity.webp";


function Home() {
    const [currentPage, setPage] = useState("");

    const renderPage = () => {
        switch(currentPage){
            case "about":
                return <About />;
            case "main":
                return <Main />;
            default:
               return <>
                    <div>
                        <h1 className='header'>Gravity Daze</h1>
                        {/* <button onClick={() => setPage("main")}>メイン</button> */}
                        {/* <Button variant='secondary' onClick={() => setPage("main")}>メイン</Button> */}
                        <Images />
                        <Info />
                    </div>
                </>
        }
    }

    return (
        <>
            {renderPage()}
        </>
    );
}


function Images(){
    return(
        <div className='imgs'>
            <img className="main" src={gravity}></img>
            {/* <img className='main' src={gravity2}></img> */}
        </div>
    );
}

const Info: React.FC = () =>
{
    // const infoRef = useRef<HTMLDivElement>(null);
    const [isVisibel, setIsVisible] = useState(false);
    
    useEffect(() => {
        const handleScroll = () => {
            console.log(window.scrollY)
            if(window.scrollY >= 1){
                setIsVisible(true);
            }
            else{
                setIsVisible(false);
            }
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        }
    }, []);

    return(
        <section className='wrapper info'>
            <h2 className='section-head'>紹介</h2>
            <div className={`info ${isVisibel ? "visible" : ""}`}>
                <p>texttexttexttexttexttexttext</p>
                <p>texttexttexttexttexttexttext</p>
                <p>texttexttexttexttexttexttext</p>
            </div>
        </section>
    )
}

const Video: React.FC = () =>
{
    return(
        <div>
            <iframe className="video" width="560" height="315" src="https://www.youtube.com/embed/xuQJGVXmvvk?si=s4QGKnm4O5S8X5ja" title="YouTube video player" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerPolicy="strict-origin-when-cross-origin" allowFullScreen></iframe>
        </div>
    )
}

export default Home;
