import React, {useRef, useEffect, useState} from "react";
import observer from "../../functions/observer";

interface Props{
    width: string;
    height: string;
}

const Video: React.FC<Props> = ({width, height}) => {
    
    const ref = useRef<HTMLIFrameElement>(null);
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const callback = function(entries: IntersectionObserverEntry[], observer: IntersectionObserver){
            if(entries[0].isIntersecting){
                setIsVisible(true);
            }
            else{
                setIsVisible(false);
            }
        };

        observer(ref, callback);
    }, [isVisible]);

    return(
        <div className={`wrapper video ${isVisible ? "visible" : ""}`}>
            <iframe width={width} height={height} src="https://www.youtube.com/embed/xuQJGVXmvvk?si=s4QGKnm4O5S8X5ja" title="YouTube video player" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerPolicy="strict-origin-when-cross-origin" allowFullScreen/>
        </div>
    )
}
