import React, { useState } from 'react';
import axios from 'axios';

function Bort() {
    const [selectedGame, setSelectedGame] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleGameSelect = (game) => {  // TypeScriptでない場合、引数の型は不要です。
        setSelectedGame(game);

        axios.post('http://localhost:5000/game_selection', { game })
            .then(() => {
                setSuccessMessage('Game selection recorded successfully');
            })
            .catch((error) => {
                console.error('Error recording game selection:', error);
                setSuccessMessage('Failed to record game selection');
            });
    };

    return (
        <div>
            <h1>Game Page</h1>
            <p>選択されたゲーム: {selectedGame}</p>
            <button onClick={() => handleGameSelect('Game1')}>Game1を選択する</button>
            <button onClick={() => handleGameSelect('Game2')}>Game2を選択する</button>
            <p>{successMessage}</p>
        </div>
    );
}

export default Bort;
