import React, { useRef, useState, useEffect, ReactNode } from "react";
import style from "../../css/Subpage.module.css"
import observer from "../../functions/observer";


interface Props{
    className: string;
    children: string;
}

const Info: React.FC<Props> = ({className, children}) => {
    const ref = useRef<HTMLDivElement>(null);
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const callback = (entries: IntersectionObserverEntry[], observer: IntersectionObserver) => {
            if(entries[0].isIntersecting){
                setIsVisible(true);
            }
            // else{
            //     setIsVisible(false);
            // }
        };

        observer(ref, callback);
    }, [isVisible]);

    const list = children.split(/\n/).filter(line => line.trim() !== "");

    return(
        <section className={`wrapper ${className} ${isVisible ? style.visible : ""}`}>
            <div ref={ref}>
                <h2>情報</h2>
                <ul>
                    {list.map((value, index) => (<li key={index}>{value}</li>))}
                </ul>
            </div>
        </section>
    )
}

export default Info;
