import React, { useState } from 'react';
import axios from 'axios';

const games = ['Game1', 'Game2', 'Game3']; // ゲームのリスト

function Bort() {
    const [selectedGame, setSelectedGame] = useState<string>('');
    const [successMessage, setSuccessMessage] = useState<string>('');

    const handleGameSelect = (game: string) => {
        setSelectedGame(game);

        axios.post('http://localhost:5000/game_selection', { game })
            .then(() => {
                setSuccessMessage('Game selection recorded successfully');
            })
            .catch((error) => {
                console.error('Error recording game selection:', error);
                setSuccessMessage('Failed to record game selection');
            });
    };

    return (
        <div>
            <h1>Game Page</h1>
            <p>選択されたゲーム: {selectedGame}</p>
            {games.map((game) => (
                <button key={game} onClick={() => handleGameSelect(game)}>
                    {game}を選択する
                </button>
            ))}
            <p>{successMessage}</p>
        </div>
    );
}

export default Bort;
