// Home.tsx
import React, {useState, useRef, useEffect} from 'react';
import About from './About';
import Main from './Main';
import Comment from "./common/Coment";
import Image from './common/Image';
import Info from "./common/Info";
import Video from "./common/Video";
import observer from "../functions/observer"
import "../css/gravity.css";
import "bootstrap/dist/css/bootstrap.min.css";
import gravity from "../img/gravity.webp";


function Gravity() {
    const [currentPage, setPage] = useState("");

    const renderPage = () => {
        switch(currentPage){
            case "about":
                return <About />;
            case "main":
                return <Main />;
            default:
               return <>
                    <div>
                        <h1 className='header'>Gravity Daze</h1>
                        {/* <button onClick={() => setPage("main")}>メイン</button> */}
                        {/* <Button variant='secondary' onClick={() => setPage("main")}>メイン</Button> */}
                        <Image className='main' src={gravity}/>
                        <Comment className='comment' sectionTitle='紹介'>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                        </Comment>
                        <Video width='800' height='500' src="https://www.youtube.com/embed/xuQJGVXmvvk?si=s4QGKnm4O5S8X5ja" title="YouTube video player" frameBorder={0} allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerPolicy="strict-origin-when-cross-origin"/>
                        <Info>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                            <p>テキストテキストテキストテキストテキスト</p>
                        </Info>
                    </div>
                </>
        }
    }

    return (
        <>
            {renderPage()}
        </>
    );
}

export default Gravity;
