import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Game {
    id: number;
    game_name: string;
    selection_count: number;
}

const Bort: React.FC = () => {
    const [games, setGames] = useState<Game[]>([]);
    const [selectedGame, setSelectedGame] = useState<string | null>(null);

    useEffect(() => {
        fetchGameSelections();
    }, []);

    const fetchGameSelections = async () => {
        try {
            const response = await axios.get('http://localhost:5000/game-selections');
            setGames(response.data);
        } catch (error) {
            console.error('Error fetching game selections:', error);
        }
    };

    const handleGameSelection = async (gameName: string) => {
        try {
            await axios.post('http://localhost:5000/select-game', { gameName });
            fetchGameSelections();
        } catch (error) {
            console.error('Error recording game selection:', error);
        }
    };

    return (
        <div>
            <h1>Select Your Favorite Game</h1>
            <ul>
                {games.map((game) => (
                    <li key={game.id}>
                        {game.game_name} - {game.selection_count} selections
                        <button onClick={() => handleGameSelection(game.game_name)}>Select</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Bort;
